import React, { useEffect, useState } from "react";
import axios from "axios";

function MeusCursos() {
    const [usuario, setUsuario] = useState(null);
    const [cursos, setCursos] = useState([]);
    const [loading, setLoading] = useState(true); // Para controlar o carregamento
    const usuarioId = 1; // ID do usuário (pode vir de um login no futuro)

    useEffect(() => {
        // Buscar informações do usuário
        axios.get(`http://localhost:5000/criar-conta/${usuarioId}`)
            .then(response => {
                setUsuario(response.data);
                setLoading(false); // Atualiza o estado de carregamento
            })
            .catch(error => {
                console.error("Erro ao buscar usuário:", error);
                setLoading(false); // Mesmo se houver erro, pare o carregamento
            });

        // Buscar os cursos que o usuário está inscrito
        axios.get(`http://localhost:5000/inscricoes/${usuarioId}`)
            .then(response => setCursos(response.data))
            .catch(error => console.error("Erro ao buscar cursos:", error));
    }, [usuarioId]);

    if (loading) {
        return <p>Carregando...</p>; // Exibe "Carregando..." enquanto a resposta não chega
    }

    return (
        <div className="Cursos">
            <h1>Visualize seus cursos aqui</h1>
            <h2>Olá {nome_usuario ? usuario.nome : "Visitante"}</h2> {/* Verifica se o nome existe */}

            <h3>Seus cursos:</h3>
            <ul>
                {cursos.length > 0 ? (
                    cursos.map((curso, index) => (
                        <li key={index}>{curso.titulo}</li>
                    ))
                ) : (
                    <p>Você ainda não se inscreveu em nenhum curso.</p>
                )}
            </ul>
        </div>
    );
}

export default MeusCursos;
