import React, { useEffect, useState } from "react";
import axios from "axios";

function MeusCursos() {
    const [usuario, setUsuario] = useState(null);
    const [cursos, setCursos] = useState([]);
    const usuarioId = 1; // ID do usuário (pode vir de um login no futuro)

    useEffect(() => {
        // Buscar informações do usuário
        axios.get(`http://localhost:5000/usuarios/${usuarioId}`)
            .then(response => setUsuario(response.data))
            .catch(error => console.error("Erro ao buscar usuário:", error));

        // Buscar os cursos que o usuário está inscrito
        axios.get(`http://localhost:5000/inscricoes/${usuarioId}`)
            .then(response => setCursos(response.data))
            .catch(error => console.error("Erro ao buscar cursos:", error));
    }, [usuarioId]);

    return (
        <div className="Cursos">
            <h1>Visualize seus cursos aqui</h1>
            <h2>Olá {usuario ? usuario.nome : "Visitante"}</h2>

            <h3>Seus cursos:</h3>
            <ul>
                {cursos.length > 0 ? (
                    cursos.map((curso, index) => (
                        <li key={index}>{curso.titulo}</li>
                    ))
                ) : (
                    <p>Você ainda não se inscreveu em nenhum curso.</p>
                )}
            </ul>
        </div>
    );
}

export default MeusCursos;
