import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import './header.module.css';
import styles from './header.module.css';

function Header() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  // Usar useEffect para verificar a autenticacao ao carregar o componente
  useEffect(() => {
    // Função para verificar a autenticidade
    const checkAuthStatus = () => {
      const storedUserName = localStorage.getItem("nome_usuario");
      setIsAuthenticated(!!storedUserName); // Se tiver nome no localStorage, o usuário está autenticado
    };

    // Verifica a autenticação sempre que o componente renderizar
    checkAuthStatus();

    // Adiciona um listener para o evento de atualização do localStorage
    window.addEventListener("storage", checkAuthStatus);

    return () => {
      // Remove o listener quando o componente for desmontado
      window.removeEventListener("storage", checkAuthStatus);
    };
  }, []);

  const handleLogout = () => {
    // Remove o nome do usuário do localStorage e atualiza o estado
    localStorage.removeItem("nome_usuario");
    setIsAuthenticated(false);
  };

  return (
    <header className={styles.navegacao}>
      <div className={styles.header}>
        <Link to='/' className={styles.logo}><h1>INFORMÁTICA</h1></Link>
      </div>

      <div className={styles.listaNavegacao}>
        <ul>
          <Link to='/meus-cursos'>
            <li className={styles.link}>Meus Cursos</li>
          </Link>
          <Link to="/sobre-nos">
            <li className={styles.link}>Sobre nós</li>
          </Link>
          <Link to='/contato'>
            <li className={styles.link}>Contato</li>
          </Link>
          {isAuthenticated ? (
            <button onClick={handleLogout} className={styles.botao_CriarConta}>Logout</button>
          ) : (
            <Link to='/criar-conta'>
              <button className={styles.botao_CriarConta}>Criar uma conta</button>
            </Link>
          )}
        </ul>
      </div>
    </header>
  );
}

export default Header;
