import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom"; // Se você estiver usando o react-router

function Header() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  useEffect(() => {
    // Verifica se o nome do usuário está no localStorage
    const storedUserName = localStorage.getItem("nome_usuario");
    setIsAuthenticated(!!storedUserName); // Se tiver nome no localStorage, o usuário está autenticado
  }, []);

  const handleLogout = () => {
    // Remove o nome do usuário do localStorage e atualiza o estado
    localStorage.removeItem("nome_usuario");
    setIsAuthenticated(false);
  };

  return (
    <header>
      <nav>
        <ul>
          <li>
            <Link to="/">Home</Link>
          </li>
          <li>
            {isAuthenticated ? (
              <button onClick={handleLogout}>Logout</button> // Se autenticado, mostra Logout
            ) : (
              <Link to="/criar-conta">Criar Conta</Link> // Se não autenticado, mostra Criar Conta
            )}
          </li>
        </ul>
      </nav>
    </header>
  );
}

export default Header;
